module.exports = {
	getMovies: function(queryParams, callback) {
		if (!callback) {
			console.error('movieApi.getMovies requires a callback');
			return;
		}

		var requestOptions = {
			url: ONCONNECT_API_URL + ONCONNECT_ENDPOINT,
			qs: queryParams
		};

		if (DEBUG) {
			handleMovieApiResponse(null, {statusCode: 200}, movieListJsonFull);
		} else {
			request(requestOptions, handleMovieApiResponse);
		}

		function handleMovieApiResponse(err, response, body) {
			if (err) {
				console.error(err);
			} else if (response.statusCode === 200) {
				var movies = JSON.parse(body, reviveJson)
				callback(movies);
			}
		}
	}
}

function reviveJson(key, value) {
	switch (key) {
		case 'dateTime':
			return moment(value, 'YYYY-MM-DDTHH:mm');
			break;
		case 'releaseDate':
			return moment(value, 'YYYY-MM-DD');
			break;
		case 'runTime':
			return moment.duration(value);
			break;
	}
	return value;
}